{
  "apiVersion": "fedlearner.k8s.io/v1alpha1",
  "kind": "FLApp",
  "metadata": {
    "name": self.name,
    "namespace": system.variables.namespace,
    "annotations":{
        "queue": "fedlearner",
        "schedulerName": "batch"
    },
    "labels": dict(system.variables.labels)
  },
  "spec": {
    "role": "Follower",
    "peerSpecs": {
      "Leader": {
        "peerURL": "",
        "authority": ""
      }
    },
    "flReplicaSpecs": {
      "Master": {
        "template": {
          "spec": {
            "restartPolicy": "Never",
            "containers": [
              {
                "env": system.basic_envs_list + [
                  {
                    "name": "STORAGE_ROOT_PATH",
                    "value": str(project.variables.storage_root_path)
                  },
                    {
                    "name": "EGRESS_URL",
                    "value": "fedlearner-stack-ingress-nginx-controller.default.svc:80"
                  },
                  {
                    "name": "EGRESS_HOST",
                    "value": project.participants[0].egress_host
                  },
                  {
                    "name": "EGRESS_DOMAIN",
                    "value": project.participants[0].egress_domain
                  },
                  {
                    "name": "APPLICATION_ID",
                    "value": self.name
                  },
                  {
                    "name": "DATA_PORTAL_NAME",
                    "value": self.name
                  },
                    {
                    "name": "DATA_PORTAL_TYPE",
                    "value": "Streaming"
                  },
                  {
                    "name": "OUTPUT_PARTITION_NUM",
                    "value": str(int(workflow.variables.num_partitions))
                  },
                  {
                    "name": "INPUT_BASE_DIR",
                    "value": str(workflow.variables.input_base_dir)
                  },
                  {
                    "name": "OUTPUT_BASE_DIR",
                    "value": str(project.variables.storage_root_path) + "/raw_data/" + self.name
                  },
                  {
                    "name": "RAW_DATA_PUBLISH_DIR",
                    "value": "portal_publish_dir/" + self.name
                  },
                  {
                    "name": "FILE_WILDCARD",
                    "value": str(workflow.variables.file_wildcard)
                  },
                  {
                    "name": "LONG_RUNNING",
                    "value": ""
                  },
                  {
                    "name": "CHECK_SUCCESS_TAG",
                    "value": ""
                  },
                  {
                    "name": "FILES_PER_JOB_LIMIT",
                    "value": str(None)
                  },
                  {
                    "name": "SINGLE_SUBFOLDER",
                    "value": ""
                  }

                ] + [],
                "imagePullPolicy": "IfNotPresent",
                "name": "tensorflow",
                "volumeMounts": list(system.variables.volume_mounts_list),
                "image": str(workflow.variables.image),
                "ports": [
                  {
                    "containerPort": 50051,
                    "name": "flapp-port",
                    "protocol": "TCP"
                  }
                ],
                "command": [
                  "/app/deploy/scripts/data_portal/run_data_portal_master.sh"
                ],
                "args": [
                ],
                "resources": {
                  "limits": {
                    "cpu": "2000m",
                    "memory": "3Gi"
                  },
                  "requests": {
                    "cpu": "2000m",
                    "memory": "3Gi"
                  }
                }
              }
            ],
            "imagePullSecrets": [
              {
                "name": "regcred"
              }
            ],
            "volumes": list(system.variables.volumes_list)
          }
        },
        "pair": False,
        "replicas": 1
      },
      "Worker": {
        "template": {
          "spec": {
            "restartPolicy": "Never",
            "containers": [
              {
                "env": system.basic_envs_list + [
                  {
                    "name": "STORAGE_ROOT_PATH",
                    "value": str(project.variables.storage_root_path)
                  },
                  {
                    "name": "APPLICATION_ID",
                    "value": self.name
                  },
                  {
                    "name": "OUTPUT_BASE_DIR",
                    "value": str(project.variables.storage_root_path) + "/data_source/" + self.name
                  },
                  {
                    "name": "EGRESS_URL",
                    "value": "fedlearner-stack-ingress-nginx-controller.default.svc:80"
                  },
                  {
                    "name": "EGRESS_HOST",
                    "value": project.participants[0].egress_host
                  },
                  {
                    "name": "EGRESS_DOMAIN",
                    "value": project.participants[0].egress_domain
                  },

                  {
                    "name": "BATCH_SIZE",
                    "value": str(1024)
                  },
                  {
                    "name": "INPUT_DATA_FORMAT",
                    "value": str(workflow.variables.input_data_format)
                  },
                  {
                    "name": "COMPRESSED_TYPE",
                    "value": ""
                  },
                  {
                    "name": "OUTPUT_DATA_FORMAT",
                    "value": "TF_RECORD"
                  },
                  {
                    "name": "BUILDER_COMPRESSED_TYPE",
                    "value": ""
                  },
                  {
                    "name": "MEMORY_LIMIT_RATIO",
                    "value": str(70)
                  },
                  {
                    "name": "OPTIONAL_FIELDS",
                    "value": ""
                  }


                ] + [],
                "imagePullPolicy": "IfNotPresent",
                "name": "tensorflow",
                "volumeMounts": list(system.variables.volume_mounts_list),
                "image": str(workflow.variables.image),
                "ports": [
                  {
                    "containerPort": 50051,
                    "name": "flapp-port",
                    "protocol": "TCP"
                  }
                ],
                "command": [
                  "/app/deploy/scripts/data_portal/run_data_portal_worker.sh"
                ],
                "args": [
                ],
                "resources": {
                  "limits": {
                    "cpu": "2000m",
                    "memory": "3Gi"
                  },
                  "requests": {
                    "cpu": "2000m",
                    "memory": "3Gi"
                  }
                }
              }
            ],
            "imagePullSecrets": [
              {
                "name": "regcred"
              }
            ],
            "volumes": list(system.variables.volumes_list)
          }
        },
        "pair": False,
        "replicas": int(workflow.variables.num_partitions)
      }
    }
  }
}

