                  {
                    "name": "END_TIME",
                    "value": str(999999999999)
                  },
                  {
                    "name": "RAW_DATA_SUB_DIR",
                    "value": "portal_publish_dir/" + str(workflow.jobs['raw-data-job-streaming'].name)
                  },
                  {
                    # not work, remove it after prepare_launch_data_join_cli been removed
                    "name": "NEGATIVE_SAMPLING_RATE",
                    "value": str(0.0)
                  }
                ]  + [],
                "imagePullPolicy": "IfNotPresent",
                "name": "tensorflow",
                "volumeMounts":
                  list(system.variables.volume_mounts_list)
                ,
                "image": str(workflow.variables.image),
                "ports": [
                  {
                    "containerPort": 50051,
                    "name": "flapp-port",
                    "protocol": "TCP"
                  }
                ],
                "command": [
                  "/app/deploy/scripts/wait4pair_wrapper.sh"
                ],
                "args": [
                  "/app/deploy/scripts/data_join/run_data_join_master.sh"
                ],
                "resources": {
                  "limits": {
                    "cpu": "2000m",
                    "memory": "3Gi"
                  },
                  "requests": {
                    "cpu": "2000m",
                    "memory": "3Gi"
                  }
                }
              }
            ],
            "imagePullSecrets": [
              {
                "name": "regcred"
              }
            ],
            "volumes":
        list(system.variables.volumes_list)

          }
        },
        "pair": true,
        "replicas": 1
      },
      "Worker": {
        "template": {
          "spec": {
            "restartPolicy": "Never",
            "containers": [
              {
                "env": system.basic_envs_list + [
                  {
                    "name": "STORAGE_ROOT_PATH",
