                  },
                  {
                    "name": "EGRESS_HOST",
                    "value": project.participants[0].egress_host
                  },
                  {
                    "name": "EGRESS_DOMAIN",
                    "value": project.participants[0].egress_domain
                  },
                  {
                    "name": "PARTITION_NUM",
                    "value": str(int(workflow.variables.num_partitions))
                  },
                  {
                    "name": "RAW_DATA_SUB_DIR",
                    "value": "portal_publish_dir/" + str(workflow.jobs['raw-data-job-streaming'].name)
