job_definitions.yaml_template:
{
  "apiVersion": "fedlearner.k8s.io/v1alpha1",
  "kind": "FLApp",
  "metadata": {
    "name": self.name,
    "namespace": system.variables.namespace,
    "labels": dict(system.variables.labels),
    "annotations":  {
      "queue": "fedlearner",
      "schedulerName": "batch",
      "min-member": "1",
      "resource-cpu": str(self.variables.worker_cpu),
      "resource-mem": str(self.variables.worker_mem),
    },
  },
  "spec": {
    "role": self.variables.role,
    "cleanPodPolicy": "All",
    "peerSpecs": {
      "Leader" if self.variables.role=="Follower" else "Follower": {
        "peerURL": "fedlearner-stack-ingress-nginx-controller.default.svc:80",
        "authority": project.participants[0].egress_host,
        "extraHeaders": {
          "x-host": "fedlearner-operator." + project.participants[0].egress_domain
        }
      }
    },
    "flReplicaSpecs": {
      "Worker": {
        "template": {
          "spec": {
            "restartPolicy": "Never",
            "containers": [
              {
                "env": system.basic_envs_list + [
                  {
                    "name": "STORAGE_ROOT_PATH",
                    "value": project.variables.storage_root_path
                  },
                  {
                    "name": "ROLE",
                    "value": self.variables.role.lower()
                  },
                  {
                    "name": "MODE",
                    "value": "train"
                  },
                  {
                    "name": "APPLICATION_ID",
                    "value": self.name
                  },
                  {
                    "name": "OUTPUT_BASE_DIR",
                    "value": project.variables.storage_root_path + "/job_output/" + self.name
                  },
                  {
                    "name": "EGRESS_URL",
                    "value": "fedlearner-stack-ingress-nginx-controller.default.svc:80"
                  },
                  {
                    "name": "EGRESS_HOST",
                    "value": project.participants[0].egress_host
                  },
                  {
                    "name": "EGRESS_DOMAIN",
                    "value": project.participants[0].egress_domain
                  },
                  {
                    "name": "CODE_KEY",
                    "value": self.variables.algorithm.path
                  },
                  {
                    "name": "DATA_PATH",
                    "value":  self.variables.data_path
                  },
                  {
                    "name": "EXPORT_PATH",
                    "value": project.variables.storage_root_path + "/job_output/" + self.name + "/exported_models"
                  },
                  {
                    "name": "EPOCH_NUM",
                    "value": self.variables.epoch_num
                  },
                  {
                    "name": "FL_STEPS_PER_SYNC",
                    "value": self.variables.steps_per_sync
                  }
                ] + list(self.variables.algorithm.config),
                "imagePullPolicy": "IfNotPresent",
                "name": "tensorflow",
                "volumeMounts": list(system.variables.volume_mounts_list),
                "image": system.variables.image_repo + "/fedlearner:" + str(self.variables.image_version),
                "ports": [
                  {
                    "containerPort": 50051,
                    "name": "flapp-port",
                    "protocol": "TCP"
                  }
                ],
                "resources": {
                  "limits": {
                    "cpu": self.variables.worker_cpu,
                    "memory": self.variables.worker_mem
                  },
                  "requests": {
                    "cpu": self.variables.worker_cpu,
                    "memory": self.variables.worker_mem
                  }
                },
                "command": [
                  "/app/deploy/scripts/wait4pair_wrapper.sh"
                ],
                "args": [
                  "/app/deploy/scripts/trainer/run_fedavg.sh"
                ]
              }
            ],
            "imagePullSecrets": [
              {
                "name": "regcred"
              }
            ],
            "volumes": list(system.variables.volumes_list)

          }
        },
        "pair": True,
        "replicas": int(1)
      }
    }
  }
}

